package basic;

public class ConditionalStmt1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int number=20;
		//want to check number is greater then 100 or not
		//1st if stmt
		if(number>100)
		{
			System.out.println("Number is greater then 100 ");
		}
		
		//2nd with if-else
		if(number>100)
		{
			System.out.println("Number is greater then 100 ");
		}
		else
		{
			System.out.println("number is less then 100");
		}
		
		
		
	}

}
