package basic;

public class ForWithBreak {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//print 1-10
		for(int i=1;i<=10;i++)
		{
			System.out.println(i);
		
			if(i==3)
			{
			
				break;
			}		
		}
		System.out.println("Out of for ");

}}
