package basic;

public class Print1_10 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Print from 1 to 10
		int i=1;
		while(i<=10)
		{
			System.out.println(i);
			i++;
		}
		//print 10 to 1
		System.out.println("Print 10 to 1");
		int j=10;
		while(j>=1)
		{
			System.out.println(j);
			j--;
		}

	}

}
